function getData(){
    var option ={
        url: '/server/tec.json',
        // crossDomain: true,
        method: 'POST',
        data: {'sound': 'eeee'},
        dataType: 'json'
    }

    $.ajax(option)
        .done(function(data) {
//            console.log(data)

            var tplOk = $('.statusOk:first')[0]
            var tplMax = $('.statusMax:first')[0]
            var tplMin = $('.statusMix:first')[0]

            $('#statuses').empty();

            $.each(data.params, function(key, param){
                console.log('param: ', param)
                /*
{
    "unit": "ob",
    "name": "3 Faktiska tikla frekvence",
    "min": 49.8,
    "max": 50.2,
    "now": 50
}
                 */
                console.log(param.min, param.now, param.max)

                /* ОГ */

                var thisTPL = $(tplOk).clone();
                if(param.now < param.min){
                    console.log('MIN')
                    thisTPL = $(tplMin).clone();
                }else if(param.now > param.max){
                    thisTPL = $(tplMax).clone();
                    console.log('MAX')
                }

                thisTPL.find('.statusText').text(param.name);
                thisTPL.find('.badge').text(param.now + param.unit);
                $('#statuses').append(thisTPL)
            });

            startInterval = true;

        })
        .fail(function( jqXHR,  textStatus,  errorThrown){
            console.log(jqXHR)
            console.log(textStatus)
            console.log(errorThrown)

        });


}
